import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Layout, ConfigProvider, theme } from 'antd';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Upload from './pages/Upload';
import Result from './pages/Result';
import History from './pages/History';
import Header from './components/Header';
import './App.css';

const { Content, Footer } = Layout;

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    // 保存token到本地存储
    if (token) {
      localStorage.setItem('token', token);
    } else {
      localStorage.removeItem('token');
    }
  }, [token]);

  const handleLogout = () => {
    setToken(null);
    setCurrentUser(null);
  };

  return (
    <ConfigProvider
      theme={{
        algorithm: theme.defaultAlgorithm,
        token: {
          colorPrimary: '#1890ff',
        },
      }}
    >
      <Router>
        <Layout className="layout" style={{ minHeight: '100vh' }}>
          <Header 
            token={token} 
            currentUser={currentUser} 
            onLogout={handleLogout} 
          />
          <Content style={{ padding: '0 50px', marginTop: 64 }}>
            <div className="site-layout-content">
              <Routes>
                <Route path="/login" element={
                  !token ? <Login setToken={setToken} setCurrentUser={setCurrentUser} /> : <Navigate to="/dashboard" />
                } />
                <Route path="/register" element={
                  !token ? <Register setToken={setToken} setCurrentUser={setCurrentUser} /> : <Navigate to="/dashboard" />
                } />
                <Route path="/dashboard" element={
                  token ? <Dashboard /> : <Navigate to="/login" />
                } />
                <Route path="/upload" element={
                  token ? <Upload /> : <Navigate to="/login" />
                } />
                <Route path="/result/:taskId" element={
                  token ? <Result /> : <Navigate to="/login" />
                } />
                <Route path="/history" element={
                  token ? <History /> : <Navigate to="/login" />
                } />
                <Route path="*" element={<Navigate to={token ? "/dashboard" : "/login"} />} />
              </Routes>
            </div>
          </Content>
          <Footer style={{ textAlign: 'center' }}>
            AI论文检测工具 ©{new Date().getFullYear()} 版权所有
          </Footer>
        </Layout>
      </Router>
    </ConfigProvider>
  );
}

export default App;
